import { Component } from '@angular/core';

@Component({
  selector: 'app-my-counter',
  templateUrl: './my-counter.component.html',
  styleUrls: ['./my-counter.component.css']
})
export class MyCounterComponent {
  count: number
  counter(action: string) {
    if (action == '+') {
      this.count++;
    }
    else if (action == '-') {
      this.count--;
    }
    else {
      this.count = 0;
    }
  }

}
