  selector: 'app-counter1',
  templateUrl: './counter1.component.html',
  styleUrls: ['./counter1.component.css']
})
export class Counter1Component {

  textInput1: any = 0;


  Counter1(action: string) {


    this.textInput1 = document.getElementsByClassName("text1");

  }

}
